mapper.select:
select e.departmentId as "departmentId", f.parameter_name as "parameter_name", e.number as "number", e.total as "total", format(e.number/e.total,4) as "percentage" from (select d.parameter_values as "departmentId", count(d.parameter_values) as "number", ( select count(*) from patient where 1=1 and if(#{startTime} is null,0=0,admissionTime >= #{startTime}) and if(#{endTime} is null,0=0,admissionTime <= #{endTime}) ) as "total" from patient a left JOIN (
) d ON a.department = d.parameter_values where 1=1 and if(#{startTime} is null,0=0,a.admissionTime >= #{startTime}) and if(#{endTime} is null,0=0,a.admissionTime <= #{endTime}) group by d.parameter_values) e left JOIN (
) f on e.departmentId = f.parameter_values

select e.departmentId as "departmentId", f.parameter_name as "parameter_name", e.number as "number", e.total as "total", format(e.number/e.total,4) as "percentage" from (select d.parameter_values as "departmentId", count(d.parameter_values) as "number", ( select count(*) from patient where leaveState = 1 and if(#{startTime} is null,0=0,leaveTime >= #{startTime}) and if(#{endTime} is null,0=0,leaveTime <= #{endTime}) ) as "total" from patient a left JOIN (
) d ON a.department = d.parameter_values where a.leaveState =1 and if(#{startTime} is null,0=0,a.leaveTime >= #{startTime}) and if(#{endTime} is null,0=0,a.leaveTime <= #{endTime}) group by d.parameter_values) e left JOIN (
) f on e.departmentId = f.parameter_values
